&lt;!DOCTYPE html&gt;
&lt;html lang="en" dir="ltr"&gt;
  &lt;head&gt;
    &lt;meta charset="utf-8"&gt;
    &lt;title&gt;Drum kit in Vue.js&lt;/title&gt;
    &lt;script src="vue.js"&gt;&lt;/script&gt;

    &lt;style media="screen"&gt;
      html {
        margin: 0;
        padding: 0;
        font-family: arial;
        font-size: 150%;
        background: url(./drummer-vue.jpg);
        background-size: cover;
      }
      h1 {
        margin: auto;
      }
      .units {
        display: inline-block;
        border: 5px solid black;
        background-color: rgba(255, 0, 0, .5);
        margin: 10px;
        padding: 5px;
        min-width: 90px;
        width: 12vh;
        text-align: center;
        transition: all 0.07s ease;
      }
      .units_play {
        transform: scale(1.3);
        border-color: rgb(255, 0, 0);
        color: rgb(255, 0, 0);
        background-color: rgba(0, 0, 0, .5);
      }
      #boxes{
        /* This is from the tutorial */
        /* display: flex;
        flex: 1;
        min-height: 100vh;
        align-items: center;
        justify-content: center; */
        width: 100%;
        margin: auto;
        text-align: center;
        position: absolute;
        transform: translate(-50%, -50%);
        top: 50%;
        left: 50%;
      }
    &lt;/style&gt;

  &lt;/head&gt;
  &lt;body&gt;

    &lt;div id="boxes"&gt;

      &lt;unit
      v-for="el in letters"
      :letter="el"
      :key="el.id"
      :class="["units" , {units_play: playing}]"&gt;
      &lt;/unit&gt;


      &lt;sound
      v-for="el in letters"
      :drum="el" :key="el.id"
      :data-key="el.dataKey"
      :src=""sounds/"+el.sound+".wav""&gt;
      &lt;/sound&gt;
    &lt;/div&gt;





    &lt;script&gt;
      Vue.component("unit", {
        props: ["letter"],
        template:
        `&lt;div :data-key="letter.dataKey"&gt;
          &lt;h1&gt;
            {{letter.text}}
          &lt;/h1&gt;
          &lt;span&gt;{{letter.sound}}&lt;/span&gt;
          &lt;/div&gt;
        `
      });
      Vue.component("sound", {
        props: ["drum"],
        template: `&lt;audio&gt;&lt;/audio&gt;`
      });
      new Vue({
        el: "#boxes",
        data: {
          letters: [
            {
              text: "A",
              dataKey: 65,
              sound: "clap"
            },
            {
              text: "S",
              dataKey: 83,
              sound: "hihat"
            },
            {
              text: "D",
              dataKey: 68,
              sound: "kick"
            },
            {
              text: "F",
              dataKey: 70,
              sound: "openhat"
            },
            {
              text: "G",
              dataKey: 71,
              sound: "boom"
            },
            {
              text: "H",
              dataKey: 72,
              sound: "ride"
            },
            {
              text: "J",
              dataKey: 74,
              sound: "snare"
            },
            {
              text: "K",
              dataKey: 75,
              sound: "tom"
            },
            {
              text: "L",
              dataKey: 76,
              sound: "tink"
            }
          ],
          playing: false
        },
        mounted: function() {
           //Create array with keys" numeric value
          var vm = this;
          var dataKeys = vm.letters.map((el)=&gt;el.dataKey);
          // Add an event listener at the global level to monitor the key presses
          window.addEventListener("keydown", function(e) {
            if (dataKeys.includes(e.keyCode)) {
              //Animation
              var animDiv = document.querySelector(`div[data-key="${e.keyCode}"]`);
              setTimeout(function() {
                animDiv.classList.remove("units_play");
              }, 50);
              //Sound play
              var playSound = document.querySelector(`audio[data-key="${e.keyCode}"]`);
              animDiv.classList.add("units_play");
              playSound.currentTime = 0;
              playSound.play();
            }
          });
        }
      });
    &lt;/script&gt;

  &lt;/body&gt;
&lt;/html&gt;
